import React, { useEffect, useState } from 'react';
import { usePlaidLink } from 'react-plaid-link';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';
import './App.css';

const MOCK_MODE = process.env.REACT_APP_MOCK_MODE === 'true';

function App() {
  const [linkToken, setLinkToken] = useState(null);
  const [accessToken, setAccessToken] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [expandedTx, setExpandedTx] = useState(null);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    if (!MOCK_MODE) {
      fetch('http://localhost:3001/api/create_link_token', { method: 'POST' })
        .then(res => res.json())
        .then(data => setLinkToken(data.link_token));
    }
  }, []);

  const { open, ready } = usePlaidLink({
    token: MOCK_MODE ? 'mock-link-token' : linkToken,
    onSuccess: async (public_token) => {
      if (MOCK_MODE) setAccessToken('mock-access-token');
      else {
        const res = await fetch('http://localhost:3001/api/exchange_public_token', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ public_token }),
        });
        const data = await res.json();
        setAccessToken(data.access_token);
      }
    },
  });

  const loadTransactions = async () => {
    const res = await fetch('http://localhost:3001/api/transactions', {
      headers: { Authorization: accessToken },
    });
    const data = await res.json();
    setTransactions(data);
  };

  // Filtrer les transactions pour le graphique
  const filteredTx = transactions.filter(tx => {
    if (filter === 'all') return true;
    if (filter === 'positive') return tx.amount > 0;
    if (filter === 'negative') return tx.amount < 0;
    return true;
  });

  // Préparer les données pour le graphique
  const chartData = filteredTx.reduce((acc, tx) => {
    const date = tx.date ? tx.date.slice(0, 7) : 'Autres'; // format YYYY-MM
    const existing = acc.find(d => d.date === date);
    if (existing) existing.amount += tx.amount;
    else acc.push({ date, amount: tx.amount });
    return acc;
  }, []);

  return (
    <div className="app-container">
      <h1 className="title">Assistant Financier</h1>

      <div className="button-group">
        <button
          className="btn btn-primary"
          onClick={() => { if (MOCK_MODE) setAccessToken('mock-access-token'); else if (ready) open(); }}
          disabled={!ready && !MOCK_MODE}
        >
          Connecter ma banque
        </button>

        {accessToken && (
          <button className="btn btn-secondary" onClick={loadTransactions}>
            Voir mes transactions
          </button>
        )}
      </div>

      {transactions.length > 0 && (
        <>
          <h2 className="section-title">Transactions</h2>
          <div className="transactions-list">
            {transactions.map(tx => (
              <div key={tx.transaction_id}>
                <div
                  className="transaction-card"
                  onClick={() => setExpandedTx(expandedTx === tx.transaction_id ? null : tx.transaction_id)}
                >
                  <div className="transaction-name">{tx.name}</div>
                  <div className={`transaction-amount ${tx.amount < 0 ? 'negative' : 'positive'}`}>
                    {tx.amount}€
                  </div>
                </div>

                {expandedTx === tx.transaction_id && (
                  <div className="transaction-details-inline">
                    <p><strong>Date:</strong> {tx.date || 'Non disponible'}</p>
                    <p><strong>Catégorie:</strong> {tx.category?.join(' > ') || 'Non disponible'}</p>
                  </div>
                )}
              </div>
            ))}
          </div>

          <h2 className="section-title">Graphique des dépenses</h2>

          <div className="chart-filters">
            <label>Filtrer :</label>
            <select value={filter} onChange={e => setFilter(e.target.value)}>
              <option value="all">Toutes</option>
              <option value="positive">Entrées</option>
              <option value="negative">Dépenses</option>
            </select>
          </div>

          <div className="chart-wrapper">
            {chartData.length > 0 && (
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={chartData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="amount" fill="#4f46e5" animationDuration={800} />
                </BarChart>
              </ResponsiveContainer>
            )}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
