import React, { useEffect, useState } from 'react';
import { usePlaidLink } from 'react-plaid-link';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';
import './App.css';

const MOCK_MODE = process.env.REACT_APP_MOCK_MODE === 'true';

function App() {
  const [linkToken, setLinkToken] = useState(null);
  const [accessToken, setAccessToken] = useState(localStorage.getItem('accessToken') || null);
  const [transactions, setTransactions] = useState([]);
  const [expandedTx, setExpandedTx] = useState(null);
  const [filterMonth, setFilterMonth] = useState('all');
  const [filterCategory, setFilterCategory] = useState('all');

  // Création link token
  useEffect(() => {
    if (!MOCK_MODE) {
      fetch('http://localhost:3001/api/create_link_token', { method: 'POST' })
        .then(res => res.json())
        .then(data => setLinkToken(data.link_token));
    }
  }, []);

  // PlaidLink
  const { open, ready } = usePlaidLink({
    token: MOCK_MODE ? 'mock-link-token' : linkToken,
    onSuccess: async (public_token) => {
      if (MOCK_MODE) {
        setAccessToken('mock-access-token');
        localStorage.setItem('accessToken', 'mock-access-token');
      } else {
        const res = await fetch('http://localhost:3001/api/exchange_public_token', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ public_token }),
        });
        const data = await res.json();
        setAccessToken(data.access_token);
        localStorage.setItem('accessToken', data.access_token);
      }
    },
  });

  const loadTransactions = async () => {
    if (!accessToken) return;
    const res = await fetch('http://localhost:3001/api/transactions', {
      headers: { Authorization: accessToken },
    });
    const data = await res.json();
    setTransactions(data);
  };

  const handleLogout = () => {
    setAccessToken(null);
    localStorage.removeItem('accessToken');
    setTransactions([]);
    setExpandedTx(null);
    setFilterMonth('all');
    setFilterCategory('all');
  };

  // Filtrage
  const filteredTransactions = transactions.filter(tx => {
    if (filterMonth !== 'all' && tx.date?.slice(0,7) !== filterMonth) return false;
    if (filterCategory !== 'all' && !tx.category?.includes(filterCategory)) return false;
    return true;
  });

  // Graphique
  const chartData = filteredTransactions.reduce((acc, tx) => {
    const date = tx.date || 'Autres';
    const existing = acc.find(d => d.date === date);
    if (existing) existing.amount += tx.amount;
    else acc.push({ date, amount: tx.amount });
    return acc;
  }, []);

  // Filtre mois et catégories
  const months = Array.from(new Set(transactions.map(tx => tx.date?.slice(0,7)))).filter(Boolean);
  const categories = Array.from(new Set(transactions.flatMap(tx => tx.category || []))).filter(Boolean);

  return (
    <div className="app-container">
      <h1 className="title">Assistant Financier</h1>

      <div className="button-group">
        {!accessToken && (
          <button
            className="btn btn-primary"
            onClick={() => { if (MOCK_MODE) { setAccessToken('mock-access-token'); localStorage.setItem('accessToken','mock-access-token'); } else if (ready) open(); }}
            disabled={!ready && !MOCK_MODE}
          >
            Connecter ma banque
          </button>
        )}

        {accessToken && (
          <>
            <button className="btn btn-secondary" onClick={loadTransactions}>
              Voir mes transactions
            </button>
            <button className="btn btn-primary" onClick={handleLogout}>
              Déconnexion
            </button>
          </>
        )}
      </div>

      {transactions.length > 0 && (
        <>
          <div className="filters">
            <select value={filterMonth} onChange={e => setFilterMonth(e.target.value)}>
              <option value="all">Tous les mois</option>
              {months.map(m => <option key={m} value={m}>{m}</option>)}
            </select>

            <select value={filterCategory} onChange={e => setFilterCategory(e.target.value)}>
              <option value="all">Toutes catégories</option>
              {categories.map(c => <option key={c} value={c}>{c}</option>)}
            </select>
          </div>

          <ul className="transactions-list">
            {filteredTransactions.map(tx => (
              <li
                key={tx.transaction_id}
                className={`transaction-card ${expandedTx === tx.transaction_id ? 'expanded' : ''}`}
                onClick={() => setExpandedTx(expandedTx === tx.transaction_id ? null : tx.transaction_id)}
              >
                <div className="transaction-header">
                  <span>{tx.name}</span>
                  <span className={tx.amount >= 0 ? 'transaction-amount positive' : 'transaction-amount negative'}>
                    {tx.amount.toFixed(2)}€
                  </span>
                </div>

                <div className="transaction-details-inline">
                  <div>Date: {tx.date || 'N/A'}</div>
                  <div>Catégorie: {tx.category?.join(', ') || 'N/A'}</div>
                  <div>Institution: {tx.institution_name || 'Sandbox Bank'}</div>
                  <div>Description: {tx.description || 'N/A'}</div>
                </div>
              </li>
            ))}
          </ul>

          <h2 className="section-title">Graphique des dépenses</h2>
          <div className="chart-wrapper">
            {chartData.length > 0 ? (
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={chartData} animationDuration={600}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="amount" fill="#4f46e5" isAnimationActive={true} />
                </BarChart>
              </ResponsiveContainer>
            ) : <p style={{ textAlign: 'center' }}>Pas de transactions pour ce filtre</p>}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
