import React, { useEffect, useState } from 'react';
import { usePlaidLink } from 'react-plaid-link';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';
import './App.css';

const MOCK_MODE = process.env.REACT_APP_MOCK_MODE === 'true';

function App() {
  const [linkToken, setLinkToken] = useState(null);
  const [accessToken, setAccessToken] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [expandedTx, setExpandedTx] = useState(null); // transaction détaillée
  const [filterMonth, setFilterMonth] = useState('all');
  const [filterCategory, setFilterCategory] = useState('all');

  // Création du link token
  useEffect(() => {
    if (!MOCK_MODE) {
      fetch('http://localhost:3001/api/create_link_token', { method: 'POST' })
        .then(res => res.json())
        .then(data => setLinkToken(data.link_token));
    }
  }, []);

  const { open, ready } = usePlaidLink({
    token: MOCK_MODE ? 'mock-link-token' : linkToken,
    onSuccess: async (public_token) => {
      if (MOCK_MODE) {
        setAccessToken('mock-access-token');
      } else {
        const res = await fetch('http://localhost:3001/api/exchange_public_token', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ public_token }),
        });
        const data = await res.json();
        setAccessToken(data.access_token);
      }
    },
  });

  // Charger les transactions
  const loadTransactions = async () => {
    const res = await fetch('http://localhost:3001/api/transactions', {
      headers: { Authorization: accessToken },
    });
    const data = await res.json();
    setTransactions(data);
  };

  // Filtrage par mois
  const filteredTransactions = transactions.filter(tx => {
    if (filterMonth !== 'all') {
      const txMonth = tx.date ? tx.date.slice(0, 7) : '';
      if (txMonth !== filterMonth) return false;
    }
    if (filterCategory !== 'all') {
      if (!tx.category || !tx.category.includes(filterCategory)) return false;
    }
    return true;
  });

  // Préparer les données pour le graphique
  const chartData = filteredTransactions.reduce((acc, tx) => {
    const date = tx.date ? tx.date : 'Autres';
    const existing = acc.find(d => d.date === date);
    if (existing) {
      existing.amount += tx.amount;
    } else {
      acc.push({ date, amount: tx.amount });
    }
    return acc;
  }, []);

  // Extraire mois uniques pour filtre
  const months = Array.from(new Set(transactions.map(tx => tx.date?.slice(0, 7)))).filter(Boolean);

  // Extraire catégories uniques
  const categories = Array.from(new Set(transactions.flatMap(tx => tx.category || []))).filter(Boolean);

  return (
    <div style={{ padding: 40, fontFamily: 'Arial, sans-serif', maxWidth: 900, margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', marginBottom: 30 }}>Assistant Financier</h1>

      <div style={{ display: 'flex', gap: 10, justifyContent: 'center', marginBottom: 20 }}>
        <button
          onClick={() => { if (MOCK_MODE) setAccessToken('mock-access-token'); else if (ready) open(); }}
          disabled={!ready && !MOCK_MODE}
          style={{ padding: '10px 20px', fontSize: 16, cursor: 'pointer' }}
        >
          Connecter ma banque
        </button>

        {accessToken && (
          <button
            onClick={loadTransactions}
            style={{ padding: '10px 20px', fontSize: 16, cursor: 'pointer' }}
          >
            Voir mes transactions
          </button>
        )}
      </div>

      {transactions.length > 0 && (
        <>
          {/* Filtres */}
          <div style={{ display: 'flex', gap: 20, marginBottom: 20, justifyContent: 'center' }}>
            <select value={filterMonth} onChange={e => setFilterMonth(e.target.value)}>
              <option value="all">Tous les mois</option>
              {months.map(m => <option key={m} value={m}>{m}</option>)}
            </select>

            <select value={filterCategory} onChange={e => setFilterCategory(e.target.value)}>
              <option value="all">Toutes catégories</option>
              {categories.map(c => <option key={c} value={c}>{c}</option>)}
            </select>
          </div>

          {/* Liste des transactions */}
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {filteredTransactions.map(tx => (
              <li
                key={tx.transaction_id}
                style={{
                  marginBottom: 10,
                  padding: 10,
                  borderRadius: 8,
                  boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
                  cursor: 'pointer',
                  transition: 'all 0.2s',
                  backgroundColor: expandedTx === tx.transaction_id ? '#f0f4ff' : '#fff'
                }}
                onClick={() => setExpandedTx(expandedTx === tx.transaction_id ? null : tx.transaction_id)}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between', fontWeight: 'bold' }}>
                  {tx.name} – {tx.amount.toFixed(2)}€
                </div>

                {/* Détails inline */}
                {expandedTx === tx.transaction_id && (
                  <div style={{ marginTop: 8, fontSize: 14, color: '#555' }}>
                    <div>Date: {tx.date || 'N/A'}</div>
                    <div>Catégorie: {tx.category?.join(', ') || 'N/A'}</div>
                    <div>Institution: {tx.institution_name || 'Sandbox Bank'}</div>
                    <div>Description: {tx.description || 'N/A'}</div>
                  </div>
                )}
              </li>
            ))}
          </ul>

          {/* Graphique dynamique */}
          <h2 style={{ textAlign: 'center', margin: '30px 0 10px' }}>Graphique des dépenses</h2>
          <div style={{ width: '100%', height: 350 }}>
            {chartData.length > 0 ? (
              <ResponsiveContainer width="100%" height="100%">
                <BarChart data={chartData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="amount" fill="#4e79a7" />
                </BarChart>
              </ResponsiveContainer>
            ) : <p style={{ textAlign: 'center' }}>Pas de transactions pour ce filtre</p>}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
